INSERT INTO `t_dx` VALUES (1, '系统公告', '<P>&nbsp;&nbsp;&nbsp; 欢迎大家登陆我站，我站主要是为广大朋友精心制作的一个系统，希望大家能够在我站获得一个好心情，谢谢！</P>\r\n<P>&nbsp;&nbsp;&nbsp; 自强不息，海纳百川，努力学习！</P>');
INSERT INTO `t_dx` VALUES (2, '系统简介', '<p class=\"MsoNormal\" style=\"text-indent:24pt;\">\r\n	本系统主要包含了等系统用户管理、车辆信息管理、用户寄件管理，留言管理模块：多个功能模块。\r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;font-family:宋体;font-size:12pt;\"> </span><span style=\"color:#000000;font-family:宋体;font-size:12pt;\"><span>本</span></span><span style=\"color:#000000;font-family:宋体;font-size:12pt;\"><span>仓库物流管理</span></span><span style=\"color:#000000;font-family:宋体;font-size:12pt;\"><span>系统是基于</span>B</span><span style=\"color:#000000;font-family:宋体;font-size:12pt;\">/</span><span style=\"color:#000000;font-family:宋体;font-size:12pt;\">S(浏览器/服务器)架构设计实现的。该</span><span style=\"color:#000000;font-family:宋体;font-size:12pt;\"><span>系统以仓库物流管理</span></span><span style=\"color:#000000;font-family:宋体;font-size:12pt;\"><span>需求为研究对象，设计并编写代码实现</span></span><span style=\"color:#000000;font-family:宋体;font-size:12pt;\"><span>仓库物流的管理功能</span></span><span style=\"color:#000000;font-family:宋体;font-size:12pt;\"><span>，</span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:24pt;\">\r\n	<br />\r\n</p>');
INSERT INTO `t_dx` VALUES (3, '关于我们', '&nbsp; &nbsp; 本公司坚持走:以质量求生存,以科技求发展,重合同守信用的道路,生产经营得到迅速发展。我们将以优质的产品和最完善的售后服务来真诚与各界朋友开展广泛的合作,共同创造一个美好的未来!<br />  <br />  公司行为准则:忠信仁义，以人为本。 <br />  忠：即忠诚，  包括三重含义：企业忠于国家、忠于民族；企业忠于客户；员工忠于企业。 <br />  信：即诚信，做企业要立足根本道德、信义，要诚实、讲信用。 <br />  仁：即仁爱，上司对下属要仁爱、体贴，同事之间要关怀、友爱。 <br />  义：即大义，公司在与合作伙伴、客户的交往过程中，不做损人利己的事，维护自己利益的前提是不损害他人利益；对民族、社会要共襄义举，要识大义，明是非。 <br />');
INSERT INTO `t_dx` VALUES (4, '联系方式', '联系人：xxxxxxxx<br />  电话：0000-0000000<br />  手机：010000000000<br />  传真：0000-0000000<br />  邮件：xxxxxxxx@163.com<br />  地址：您公司的地址<br />  网址：http://www.xxxx.com<br />');
INSERT INTO `t_dx` VALUES (5, 'null', 'null');

